function get_tlv(data) {
  var obj = {};
  var tlv;
  try {
    tlv = TLV.parse(data);
  } catch (e) {
    obj.stat = 'fail';
    obj.message = e.toString();
    
    return obj;
  }

  obj.stat = 'ok';
  //obj.tlv = simplify(tlv);
  obj.tlv = tlv;
  return obj;
}

// '/parse/:data'
function parse(req, res) {
  
  var data = req.params['data'];
  var obj = {};
  if (data === undefined) {
    obj.stat = 'fail';
    res.json(obj);
    return;
  }
  
  obj = get_tlv(data);
  
  res.json(obj);
}

exports.parse = parse;

module.exports = {
  parse: parse,
  get_tlv: get_tlv
};